#!/bin/bash
ADMIN_ACCOUNT_IDENTIFIER_32_BYTES_IN_HEX="..."
dex canister create mydaocoin
dfx build mydaocoin
eval dfx canister install mydaocoin --argument="'(\"data:image/jpeg;base64,\", \"mydao coin\", \"MDC\", 8, 1000000000000000000, principal \"$ADMIN_ACCOUNT_IDENTIFIER_32_BYTES_IN_HEX\", 10000)'"
eval dfx canister call mydaocoin balanceOf "'(principal \"$ADMIN_ACCOUNT_IDENTIFIER_32_BYTES_IN_HEX\")'"